/* import logo from './logo.svg';
import './App.css'; */
import { useState, useRef, useEffect } from 'react';
import ImageEditor from './ImageEditor.js';

function App() {
  //refs
  const canvas = useRef(null);
  const fileInput = useRef(null);

  const [editor, setEditor] = useState(null);
  useEffect(()=>{
    setEditor(new ImageEditor(canvas.current));
  },[]);

  return (
    <>
      <h1>Images</h1>
      <div id="add-section">

      </div>
      <div id="images">

      </div>
      <dialog id="image-editor">
        <div class="content">
          <h2>Image Editor</h2>
          <div class="toolbar">
            <div class="zoom">
              <button class="zoom-in" title="Zoom in"></button>
              <button class="zoom-out" title="Zoom out"></button>
            </div>
            <div class="actions">
              <button data-state="view" class="view">View</button>
              <button data-state="crop" class="crop">Crop</button>
              <button data-state="rotate" class="rotate">Rotate</button>
              <div class="rotate-actions">
                <button data-rotate="-1" class="counter-clockwise">-90&deg;</button>
                <button data-rotate="1" class="clockwise">+90&deg;</button>
              </div>
            </div>
            <div class="other">
              <button class="trash" title="Delete"></button>
            </div>
          </div>
          <canvas id="image-edit-screen" ref={canvas}></canvas>
        </div>
        <footer class="buttons">
          <button class="save">Save</button>
          <button class="cancel" >Cancel</button>
          <input type="file" id="image-file" ref={fileInput} accept="image/png, image/jpeg, image/gif, image/bmp, image/webp" />
          <label htmlFor="image-file" className="replace button">Replace Image</label>
        </footer>
      </dialog>
    </>
  );
}

export default App;
