/* import logo from './logo.svg';
import './App.css'; */
import { useState, useRef, useEffect } from 'react';
import ImageEditor from './ImageEditor.js';
import { createPortal } from 'react-dom';
import ModalMessage from './ModalMessage.js';

function App() {
  //refs
  const canvas = useRef(null);
  const fileInput = useRef(null);
  const editorDialog = useRef(null);

  const [editor, setEditor] = useState(null);
  const [isMessageOpen, setIsMessageOpen] = useState(false);
  const [messageText, setMessageText] = useState('');

  //outside things
  //this.edit_image_button.addEventListener('click', this.initiate_edit_image.bind(this));
  function dragover(e){
    e.preventDefault();
  }
  function drop(e){
    e.preventDefault();
    if (e.dataTransfer.items && e.dataTransfer.items[0].kind === 'file'){
        // Use DataTransferItemList interface to access the file(s)
        let file = e.dataTransfer.items[0].getAsFile();
        console.log(file);


        if (editor.image_types.includes(file.type)){
            read_drop_image(file);
        }else if (file.size > editor.max_filesize){
          setMessage(editor.file_too_big());
        }else{
          setMessage(editor.invalid_file());
        }
    }
  }
  function read_drop_image(file){
    console.log('drop image', file);
    const reader = new FileReader();
    editorDialog.current.showModal();
    reader.addEventListener("load", set_image_data, false);
    reader.readAsDataURL(file);
  }
  function replace_image(e){
    console.log('replace_image', e.target.files);
    const file = e.target.files[0];
    console.log(file);
    if (file && editor.image_types.includes(file.type)){
        const reader = new FileReader();
        if (!editorDialog.current.open){
          editorDialog.current.showModal();
        }

        reader.addEventListener("load", set_image_data, false);
        if (file) {
            reader.readAsDataURL(file);
        }
    }else if(file && file.size > editor.max_filesize){
      setMessage(editor.file_too_big());
    }else if(file){
      setMessage(editor.invalid_file());
    }else{
      setMessage('No file found');
    }
  }
  // convert image file to base64 string
  function set_image_data(e){
    editor.load_image(e.target.result);
  }


  //for the modal message
  function openMessage(){
    setIsMessageOpen(true);
  }
  function closeMessage(){
    console.log('close Message!!!!!');
    setIsMessageOpen(false);
  }
  function setMessage(text){
    openMessage();
    setMessageText(text);
  }




  useEffect(()=>{
    setEditor(new ImageEditor(canvas.current));
  },[]);

  return (
    <>
      <div id="drop-zone" onDragOver={dragover} onDrop={drop}>
        <strong>Drag and Drop Image Here<br />
        or</strong>
        <label htmlFor="image-file">Select Image</label>
      </div>
      <h1>Images</h1>
      <div id="images">

      </div>
      <dialog id="image-editor" ref={editorDialog}>
        <div className="content">
          <h2>Image Editor</h2>
          <div className="toolbar">
            <div className="zoom">
              <button className="zoom-in" title="Zoom in"></button>
              <button className="zoom-out" title="Zoom out"></button>
            </div>
            <div className="actions">
              <button data-state="view" className="view">View</button>
              <button data-state="crop" className="crop">Crop</button>
              <button data-state="rotate" className="rotate">Rotate</button>
              <div className="rotate-actions">
                <button data-rotate="-1" className="counter-clockwise">-90&deg;</button>
                <button data-rotate="1" className="clockwise">+90&deg;</button>
              </div>
            </div>
            <div className="other">
              <button className="trash" title="Delete"></button>
            </div>
          </div>
          <canvas id="image-edit-screen" ref={canvas}></canvas>
        </div>
        <footer className="buttons">
          <button className="save">Save</button>
          <button className="cancel" >Cancel</button>
          <input type="file" id="image-file" ref={fileInput} accept="image/png, image/jpeg, image/gif, image/bmp, image/webp" onChange={replace_image} />
          <label htmlFor="image-file" className="replace button">Replace Image</label>
        </footer>
      </dialog>
      <ModalMessage isOpen={isMessageOpen} onClose={closeMessage} children={messageText}></ModalMessage>
    </>
  );
}

export default App;
